function calculateDurationhour() {

var hourFrom = parseFloat(document.getElementById("hourFrom").value);
var hourTo = parseFloat(document.getElementById("hourTo").value);
var durationField = document.getElementById("duration");

if (hourFrom < hourTo) {
var duration = hourTo - hourFrom;
durationField.value = duration.toString();
} else {

var duration = (24 - hourFrom) + hourTo;
durationField.value = duration.toString();
}

}

