function showCheckboxes() {

var holidayStatus = document.getElementById("time_off_type").value;
var checkboxDiv = document.getElementById("checkboxDiv");
var halfDayCheckbox = document.getElementById("halfDay");
var Days = document.getElementById("days");
var Duration = document.getElementById("duration");
var fullDayCheckbox = document.getElementById("fullDay");
var dateToDiv = document.getElementById('date_to');
var datefromDiv = document.getElementById('date_from');
var halfDaySelections = document.getElementById("halfDaySelections");
var hourFrom = document.getElementById('hourFromSelection');
var hourTo = document.getElementById('hourToSelection');
var attachment = document.getElementById('attachment');

if (Duration.value >= 3 & holidayStatus === "2" & attachment !== null) {
attachment.required = true;
}

else {
if (attachment !== null) {
attachment.required = false;
}
}

if (holidayStatus === "4") {
checkboxDiv.style.display = "block";
} else {
checkboxDiv.style.display = "none";
halfDaySelections.style.display = "none";
dateToDiv.style.display = "block";
halfDayCheckbox.checked = false;
fullDayCheckbox.checked = false;
dateToDiv.value = datefromDiv.value;
}
}

function calculateDuration() {
var dateFrom = new Date(document.getElementById('date_from').value);
var dateTo = new Date(document.getElementById('date_to').value);
var attachment = document.getElementById('attachment');
var duration = document.getElementById('duration');
var holidayStatus = document.getElementById("time_off_type").value;

if (dateFrom & dateTo) {
var timeDiff = Math.abs(dateTo.getTime() - dateFrom.getTime());
var diffDays = Math.ceil(timeDiff / (1000 * 3600 * 24)) + 1;

if (duration.value >= 3 & holidayStatus === "2" & attachment !== null) {
attachment.required = true;
}

else {
if (attachment !== null) {
attachment.required = false;
}
}

duration.value = diffDays;
}
}
